function backward (lSpeed: number, rSpeed: number) {
    maqueen.motorRun(maqueen.Motors.M1, maqueen.Dir.CCW, lSpeed)
    maqueen.motorRun(maqueen.Motors.M2, maqueen.Dir.CCW, rSpeed)
}
function forward (lSpeed: number, rSpeed: number) {
    maqueen.motorRun(maqueen.Motors.M1, maqueen.Dir.CW, lSpeed)
    maqueen.motorRun(maqueen.Motors.M2, maqueen.Dir.CW, rSpeed)
}
function right (lSpeed: number, rSpeed: number) {
    maqueen.motorRun(maqueen.Motors.M1, maqueen.Dir.CW, lSpeed)
    maqueen.motorRun(maqueen.Motors.M2, maqueen.Dir.CCW, rSpeed)
}
function left (lSpeed: number, rSpeed: number) {
    maqueen.motorRun(maqueen.Motors.M1, maqueen.Dir.CCW, lSpeed)
    maqueen.motorRun(maqueen.Motors.M2, maqueen.Dir.CW, rSpeed)
}
bluetooth.onBluetoothConnected(function () {
    basic.showString("C")
})
bluetooth.onBluetoothDisconnected(function () {
    basic.showString("D")
})
bluetooth.onUartDataReceived(serial.delimiters(Delimiters.NewLine), function () {
    RX_Data = bluetooth.uartReadUntil(serial.delimiters(Delimiters.NewLine))
    if (RX_Data.compare("F") == 0) {
        basic.showString("F")
        forward(200, 200)
    } else if (RX_Data.compare("B") == 0) {
        basic.showString("B")
        backward(192, 192)
    } else if (RX_Data.compare("L") == 0) {
        basic.showString("L")
        left(128, 128)
    } else if (RX_Data.compare("R") == 0) {
        basic.showString("R")
        right(128, 128)
    } else {
        if (RX_Data.compare("S") == 0) {
            basic.showString("S")
            maqueen.motorStop(maqueen.Motors.All)
        }
    }
})
let RX_Data = ""
basic.showString("BLE")
bluetooth.startTemperatureService()
bluetooth.startUartService()
RX_Data = ""
